svg.script:
(function() { // 使用 IIFE (立即执行函数表达式) 避免污染全局作用域
  // 注意：在 SVG 内部，document 指向 SVG 文档本身
  const svg = document.getElementById('animated-svg');
  // 在 SVG 文档中查找 .frame 元素
  const frames = document.querySelectorAll('.frame');
  let currentFrameIndex = 0; // 跟踪当前显示的帧索引 (0-7)
  let animationInterval = null; // 存储定时器ID
  const frameDelay = 150; // 每帧之间的延迟（毫秒），可以根据需要调整

  // 显示指定索引的帧，隐藏其他帧
  function showFrame(index) {
    frames.forEach((frame, i) => {
      if (i === index) {
        frame.classList.add('current-frame');
        frame.style.display = 'block'; // 确保显示
      } else {
        frame.classList.remove('current-frame');
        frame.style.display = 'none'; // 确保隐藏
      }
    });
    currentFrameIndex = index;
  }

  // 正向播放动画 (从当前帧到第8帧)
  function playForward() {
    // 清除可能存在的反向动画
    if (animationInterval) {
      clearInterval(animationInterval);
      animationInterval = null;
    }
    // 从当前帧的下一帧开始播放
    let nextFrameIndex = currentFrameIndex + 1;
    if (nextFrameIndex < frames.length) {
      animationInterval = setInterval(() => {
        showFrame(nextFrameIndex);
        nextFrameIndex++;
        // 如果已经播放到最后一帧，则停止
        if (nextFrameIndex >= frames.length) {
          clearInterval(animationInterval);
          animationInterval = null;
        }
      }, frameDelay);
    }
  }

  // 反向播放动画 (从当前帧回到第1帧)
  function playReverse() {
    // 清除可能存在的正向动画
    if (animationInterval) {
      clearInterval(animationInterval);
      animationInterval = null;
    }
    // 从当前帧的前一帧开始播放
    let prevFrameIndex = currentFrameIndex - 1;
    if (prevFrameIndex >= 0) {
      animationInterval = setInterval(() => {
        showFrame(prevFrameIndex);
        prevFrameIndex--;
        // 如果已经播放到第一帧，则停止
        if (prevFrameIndex < 0) {
          clearInterval(animationInterval);
          animationInterval = null;
        }
      }, frameDelay);
    }
  }

  // 确保 DOM 加载完毕 (虽然 SVG 内部脚本通常在解析后执行，但加上更保险)
  // 对于内联 SVG 脚本，window.onload 或 DOMContentLoaded 可能不适用或行为不同
  // 直接执行初始化逻辑通常就足够了，因为脚本是在所有元素解析后执行的
  // 初始化：确保第一帧是显示的
  showFrame(0);

  // 添加事件监听器到 SVG 元素
  // 检查元素是否存在再添加监听器
  if (svg) {
     svg.addEventListener('mouseenter', playForward);
     svg.addEventListener('mouseleave', playReverse);
  }
})(); // 立即执行
